import React, { useEffect, useState } from "react";
import {useParams, Link} from "react-router-dom"
import TimeForm from "./TimeForm";

const TimeFormEdit = (props) => {
  console.log(props);
const params = useParams()
console.log(params)
  const [formState, setFormState] = useState({
    date: "",
    staff_attendance: "",
    client: "",
    travel_information: "",
    arrival_time: "",
    departure_time:"",
    products_used:"",
    receipts: "" , 
    notes: "",
    action_next_visit: "",
    written_by: ""
  });

  const [timesheetEdit, setTimesheetEdit] = useState({
    date: "",
staff_attendance: "",
client: "",
travel_information: "",
arrival_time: "",
departure_time:"",
products_used:"",
receipts: "" , 
notes: "",
action_next_visit: "",
written_by: "",
  });
  const [timesheetDelete, setTimesheetDelete] = useState({
    date: "",
    staff_attendance: "",
    client: "",
    travel_information: "",
    arrival_time: "",
    departure_time:"",
    products_used:"",
    receipts: "" , 
    notes: "",
    action_next_visit: "",
    written_by: "",
  });

  // useEffect(() => {
  //   setFormState(props.timesheet);
  //   console.log("useEffect edit");
  // }, [props.timesheet]);

  const handleChange = (e) => {
    const newState = { ...formState };
    newState[e.target.name] = e.target.value;
    setFormState(newState);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("handleSubmit");
    props.submit(formState);
  };

  const handleTimeFormSubmit = (formState) => {
    const newTime = {
      date: formState.date,
      staff_attendance: formState.staff_attendance,
      client: formState.client,
      travel_information: formState.travel_information,
      arrival_time: formState.arrival_time,
      departure_time: formState.departure_time,
      products_used: formState.products_used,
      receipts: formState.receipts,
      notes: formState.notes,
      action_next_visit: formState.action_next_visit,
      written_by: formState.written_by
    };
    console.log(newTime);
    const newTimesheets = [...timesheets];
    newTimesheets.push(newTime);

    setTimesheets(newTimesheets);

    fetch("http://localhost:3000/api/timesheets", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTime),
    }).then((response) => {
      console.log("use clases: response:", response);
    });
  };

  const handleTimesheetClick = (id) => {
    console.log(id);
    fetch(`http://localhost:3000/api/timesheets/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((timesheetData) => {
        console.log("timesheetData:", timesheetData);
        setTimesheetEdit(timesheetData);
        setTimesheetDelete(timesheetData);
      });
      return (
        <div>

        <h1>Timesheets</h1>
        <ul>
          {formState.map((el, index) => (
            <li
              key={index}
            >
              <Link to={`timesheet/edit/${el.id}`}>{el.client} </Link>
            </li>
          ))}
        </ul>
      </div>
      )

  };




  const handleDelete = () => {
    // e.preventDefault();
    props.onDelete(formState.id);
    fetch(`http://localhost:3000/api/timesheets/${formState.id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((timesheetData) => {
        console.log("timesheetData:", timesheetData);

      });
  };

  return (

    <div>
<TimeForm submit={handleTimeFormSubmit} timesheet={timesheets} />

    <h2>Edit/Delete</h2>
    <form onSubmit={handleSubmit}>

      <div>
      <label>
      <div className="datetxt">Date</div> 
        <input
          name="date"
          value={formState.date}
          onChange={handleChange}
        ></input>
      </label>
      </div>

      <div>
      <label>
      <div className="clienttxt">Client</div> 
        <input
          name="client"
          value={formState.client}
          onChange={handleChange}
        ></input>
      </label>
      </div>



      <div>
      <label>
      <div className="stafftxt">Staff Attendance</div> 
        <input
          name="staff_attendance"
          value={formState.staff_attendance}
          onChange={handleChange}
        ></input>
      </label>
      </div>

     <div>
      <label>
      <div className="traveltxt">Travel Information</div> 
        <input
          name="travel_information"
          value={formState.travel_information}
          onChange={handleChange}
        ></input>
      </label>
      </div>


<div>
      <label>
      <div className="arrivaltxt">Arrival Time</div> 
        <input
          name="arrival_time"
          value={formState.arrival_time}
          onChange={handleChange}
        ></input>
      </label>
      </div>

        <div>
      <label>
      <div className="departuretxt">Departure Time</div> 
        <input
          name="departure_time"
          value={formState.departure_time}
          onChange={handleChange}
        ></input>
      </label>
      </div>

<div>
      <label>
      <div className="productstxt">Products Used</div> 
        <input
          name="products_used"
          value={formState.products_used}
          onChange={handleChange}
        ></input>
      </label>
      </div>

<div>
      <label>
      <div className="receiptstxt">Receipts</div> 
        <input
          name="receipts"
          value={formState.receipts}
          onChange={handleChange}
        ></input>
      </label>
      </div>

<div>
      <label>
      <div className="notestxt">Notes</div> 
        <input
          name="notes"
          value={formState.notes}
          onChange={handleChange}
        ></input>
      </label>
      </div>

<div>
      <label>
      <div className="actiontxt">Action Next Visit</div> 
        <input
          name="action_next_visit"
          value={formState.action_next_visit}
          onChange={handleChange}
        ></input>
      </label>
      </div>

<div>
      <label>
      <div className="writtentxt">Written By</div> 
        <input
          name="written_by"
          value={formState.written_by}
          onChange={handleChange}
        ></input>
      </label>
      </div>
        <button className ="edit" type="submit" >Edit</button>
        <button className ="delete"onClick={handleDelete}>Delete</button>
      </form>
    </div>
  );
};

export default TimeFormEdit;
