import TimeContainer from "./components/AddTimeForm";
import TimeFormEdit from "./components/TimeFormEdit";
import { UserForm } from "./components/User/UserForm";
import { List } from "./components/List";
import AddTimeForm from "./components/AddTimeForm";
import { NewUser } from "./components/User/NewUser";
import {
  BrowserRouter as Router,
  Link,
  Switch,
  Route,
  useHistory,
} from "react-router-dom";
import { useState } from "react";
import "./Style.css";
import { MenuItem } from "@material-ui/core";
import { Menu } from "@material-ui/core";
import axios from "axios";
import React from "react";
import Button from "@material-ui/core/Button";
import { emphasize, withStyles } from '@material-ui/core/styles';
import Breadcrumbs from '@material-ui/core/Breadcrumbs';
import Chip from '@material-ui/core/Chip';
import HomeIcon from '@material-ui/icons/Home';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const StyledBreadcrumb = withStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.grey[100],
    height: theme.spacing(3),
    color: theme.palette.grey[800],
    fontWeight: theme.typography.fontWeightRegular,
    '&:hover, &:focus': {
      backgroundColor: theme.palette.grey[300],
    },
    '&:active': {
      boxShadow: theme.shadows[1],
      backgroundColor: emphasize(theme.palette.grey[300], 0.12),
    },
  },
}))


export const App = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    // setAnchorEl(event.currentTarget);
    event.preventDefault();
  console.info('You clicked a breadcrumb.');
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  //  const name = JSON.parse(localStorage.getItem('name'));
  //  console.log(name)

  const [loggedIn, setLoggedIn] = useState(false);
  const [loggedInName, setLoggedInName] = useState("");
  const onLogOut = () => {
    setLoggedIn(false);
    localStorage.removeItem("token");
  };

  return (
    <Router>
      <div>
        <div>

        <Breadcrumbs aria-label="breadcrumb">
      <StyledBreadcrumb
        component="a"
        href="#"
        label="Home"
        icon={<HomeIcon fontSize="small" />}
        onClick={handleClick}
      />
      <StyledBreadcrumb component="a" href="#" label="Catalog" onClick={handleClick} />
      <StyledBreadcrumb
        label="Accessories"
        deleteIcon={<ExpandMoreIcon />}
        onClick={handleClick}
        onDelete={handleClick}
      />
    </Breadcrumbs>

          <div class="dropdown">
            <button
              class="btn btn-secondary dropdown-toggle"
              anchorEl={anchorEl}
              keepMounted
              open={Boolean(anchorEl)}
              type="button"
              onClose={handleClose}
              onClick={handleClick}
              id="dropdownMenuButton"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Menu
            </button>
          </div>
          <div className="menu"></div>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            {loggedIn && (
              <MenuItem
                onClick={handleClose}
                component={Link}
                className="list-group-item"
                to={"/home"}
              >
                Existing Timesheets
              </MenuItem>
            )}
            {loggedIn && (
              <MenuItem
                onClick={handleClose}
                component={Link}
                className="list-group-item"
                to={"/add"}
              >
                Add New Timesheet
              </MenuItem>
            )}
            {!loggedIn && (
              <MenuItem
                onClick={handleClose}
                component={Link}
                className="list-group-item"
                to={"/"}
              >
                Login
              </MenuItem>
            )}
            {loggedIn && (
              <MenuItem
                onClick={handleClose}
                component={Link}
                className="list-group-item"
                onClick={onLogOut}
                to={"/logout"}
              >
                Logout
              </MenuItem>
            )}
          </Menu>
        </div>
        <Switch>
          <Route exact path="/login">
            <UserForm
              setLoginHook={setLoggedIn}
              setLoggedInName={setLoggedInName}
            />
          </Route>
          <Route exact path="/signup">
            <NewUser />
          </Route>

          <Route exact path="/logout">
            <UserForm
              setLoginHook={setLoggedIn}
              setLoggedInName={setLoggedInName}
            />
          </Route>
          <Route exact path="/add">
            <AddTimeForm />
          </Route>
          <Route exact path="/timesheet/edit/:id">
            <TimeFormEdit />
          </Route>
          <Route exact path="/">
            <UserForm
              setLoginHook={setLoggedIn}
              setLoggedInName={setLoggedInName}
            />
          </Route>
          <Route exact path="/home">
            <h3 className="welcome">{`Welcome, ${loggedInName}!`}</h3>

            <List />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};
