import TimeContainer from "./components/AddTimeForm";
import TimeFormEdit from "./components/TimeFormEdit";
import {UserForm} from "./components/User/UserForm"
import {List} from "./components/List"; 
import AddTimeForm from "./components/AddTimeForm";
import {NewUser} from "./components/User/NewUser"
import { BrowserRouter as Router, Link, Switch, Route, useHistory } from "react-router-dom";
import { useState } from "react";
import "./Style.css";
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import { makeStyles } from '@material-ui/core/styles';
import { Tabs } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export const App = () => {
  const classes = useStyles();

 const name = JSON.parse(localStorage.getItem('name'));
 console.log(name)

const [loggedIn, setLoggedIn] = useState(false)  
  const onLogOut = () => {

    setLoggedIn(false);
  localStorage.removeItem('token')
}

  return (


<Router>


  <Toolbar>
    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
      <MenuIcon />
    </IconButton>
    <Typography variant="h6" className={classes.title}>
    {/* <Tabs label="Menu Item 1" component={Link} to="/" />
    <Tabs label="Menu Item 2" component={Link} to="/" />
    <Tab label="Menu Item 3" component={Link} to="/" /> */}

    </Typography>
    <Button color="inherit">Login</Button>
  </Toolbar>




    <div>
        {loggedIn && <Link className="navBar" to="/home">Home</Link>}
        {loggedIn && <Link className="navBar" to="/add">Add</Link>}
        {!loggedIn && <Link className="navBar" to="/">Login</Link>}
        {loggedIn && <Link className="navBar"  onClick={onLogOut} to="/logout">Logout</Link>}
        <Switch>
        <Route exact path="/login">
            <UserForm setLoginHook={setLoggedIn}/>
          </Route>
          <Route exact path="/signup">
            <NewUser />
          </Route>

          <Route exact path="/logout">
            <UserForm setLoginHook={setLoggedIn}/>
          </Route>
          <Route exact path="/add">
          {/* <h2>Welcome, {'name'}!</h2> */}
            <AddTimeForm />
          </Route>
          <Route exact path="/timesheet/edit/:id" >
          {/* <h2>Welcome, {name}!</h2> */}
            <TimeFormEdit />
          </Route>
          <Route exact path="/">
            <UserForm setLoginHook={setLoggedIn} />
          </Route>
          <Route exact path="/home">
          {/* <h2>Welcome, {name}!</h2> */}

           <List /> 
          </Route>
        </Switch>
    </div>
    </Router>
  );
};
