import React, { useState } from "react";


const TimeForm = (props) => {

  const [formState, setFormState] = useState({
    date: "",
staff_attendance: "",
client: "",
travel_information: "",
arrival_time: "",
departure_time:"",
products_used:"",
receipts: "" , 
notes: "",
action_next_visit: "",
written_by: ""
  })


  const handleChange = (e) => {
    const newState = { ...formState }
    newState[e.target.name] = e.target.value;
    setFormState(newState);
  }

  const handleSubmit = (e) => {
    // e.preventDefault();
    console.log("handleSubmit");
    props.submit(formState.name, formState.time, formState.notes, formState.completed);
  };

  return (
    <div className="addDiv">
      <h2>Add</h2>
      <form onSubmit={handleSubmit}>

        <div className="addName"> 
        <label>
        <div className="nametext">Name</div> 
          <input name="name" value={formState.name} onChange={handleChange}></input>
        </label>
        </div>

        <div className="addTime">
        <label>
        <div className="timetext">Time</div> 
          <input name="time" value={formState.time} onChange={handleChange}></input>
        </label>
        </div>
<div className="addNotes"> 

        <label>
        <div className="notestext">Notes</div>   
          <input
            name="notes"
            value={formState.notes}
            onChange={handleChange}
          ></input>
          </label>
          </div>
          <div className="addCompleted">
          <label> 
          <div className="completedtext">Completed</div> 
           <input
             id="flexSwitchCheckDefault"
            value={formState.completed}
            onChange={handleChange}
          ></input>
          </label>
          </div>
        <button className="add" type="submit">Add</button>
      </form>
    </div>
  );
};

export default TimeForm;
