import TimeContainer from "./components/AddTimeForm";
import TimeFormEdit from "./components/TimeFormEdit";
import {UserForm} from "./components/User/UserForm"
import {List} from "./components/List"; 
import AddTimeForm from "./components/AddTimeForm";
import { BrowserRouter as Router, Link, Switch, Route, useHistory } from "react-router-dom";
import { useState } from "react";
import "./Style.css";




export const App = () => {
  const history = useHistory()
  console.log(history);
const [loggedIn, setLoggedIn] = useState(false)  
  const onLogOut = () => {

    setLoggedIn(false);
  localStorage.removeItem('token')
  history.replace('/')
}

  return (
<Router>
    <div>
      <Link className="navBar" to="/home">Home</Link>
        <Link className="navBar" to="/add">Add</Link>
        <Link className="navBar" to="/">Login</Link>
        {loggedIn && <Link className="navBar"  onClick={onLogOut} to="/logout">Logout</Link>}
        <Switch>
        <Route exact path="/login">
            <UserForm setLoginHook={setLoggedIn}/>
          </Route>

          <Route exact path="/logout">
            <UserForm />
          </Route>
          <Route exact path="/add">
            <AddTimeForm />
          </Route>
          <Route exact path="/timesheet/edit/:id" >
            <TimeFormEdit />
          </Route>
          <Route exact path="/">
            <UserForm setLoginHook={setLoggedIn} />
          </Route>
          <Route exact path="/home">
            <h1></h1>
           <List /> 
          </Route>
        </Switch>
    </div>
    </Router>
  );
};
