import React from "react";
import { withRouter } from "react-router-dom";  
import TimeContainer from "./components/AddTimeForm";
import TimeFormEdit from "./components/TimeFormEdit";
import {UserForm} from "./components/User/UserForm"
import {List} from "./components/List"; 
import AddTimeForm from "./components/AddTimeForm";
import {NewUser} from "./components/User/NewUser"
import { BrowserRouter as Router, Link, Route, Switch, useHistory } from "react-router-dom";
import { useState } from "react";
import "./Style.css";
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export const App = (props) => {
  const { history } = props
 const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);


  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClick = (pageURL) => {
    history.push(pageURL); 
    setAnchorEl(null);
  };

 const name = JSON.parse(localStorage.getItem('name'));
 console.log(name)

const [loggedIn, setLoggedIn] = useState(false)  
  const onLogOut = () => {

    setLoggedIn(false);
  localStorage.removeItem('token')
}

  return (


<Router>

<div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
          </Typography>
            <div>
              <IconButton 
              edge="start" 
               className={classes.menuButton} 
               color="inherit" 
               aria-label="menu"
                onClick={handleMenu}
              >
                <MenuIcon />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={() => handleMenuClick(null)}
              >
                <MenuItem onClick={() => handleMenuClick('/')}>Home</MenuItem>
                <MenuItem onClick={() => handleMenuClick('add')}>Add</MenuItem>
                <MenuItem onClick={() => handleMenuClick('login')}>Login</MenuItem>
                <MenuItem onClick={() => handleMenuClick('logout')}>Logout</MenuItem>
              </Menu>
            </div>
        </Toolbar>
      </AppBar>
    </div>



    <div>
        {loggedIn && <Link className="navBar" to="/home">Home</Link>}
        {loggedIn && <Link className="navBar" to="/add">Add</Link>}
        {!loggedIn && <Link className="navBar" to="/">Login</Link>}
        {loggedIn && <Link className="navBar"  onClick={onLogOut} to="/logout">Logout</Link>}
        <Switch>
        <Route exact path="/login">
            <UserForm setLoginHook={setLoggedIn}/>
          </Route>
          <Route exact path="/signup">
            <NewUser />
          </Route>

          <Route exact path="/logout">
            <UserForm setLoginHook={setLoggedIn}/>
          </Route>
          <Route exact path="/add">
          {/* <h2>Welcome, {'name'}!</h2> */}
            <AddTimeForm />
          </Route>
          <Route exact path="/timesheet/edit/:id" >
          {/* <h2>Welcome, {name}!</h2> */}
            <TimeFormEdit />
          </Route>
          <Route exact path="/">
            <UserForm setLoginHook={setLoggedIn} />
          </Route>
          <Route exact path="/home">
          {/* <h2>Welcome, {name}!</h2> */}

           <List /> 
          </Route>
        </Switch>
    </div>
    </Router>
  );
};
