import React, { useEffect, useState } from "react";

const TimeFormEdit = (props) => {
  console.log(props);

  const [formState, setFormState] = useState({
    name: "",
    time: "",
    notes: "",
    completed: "",
    _id: "",
  });

  useEffect(() => {
    setFormState(props.timesheet);
    console.log("useEffect edit");
  }, [props.timesheet]);

  const handleChange = (e) => {
    const newState = { ...formState };
    newState[e.target.name] = e.target.value;
    setFormState(newState);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("handleSubmit");
    props.submit(formState);
  };

  const handleDelete = () => {
    // e.preventDefault();
    props.onDelete(formState._id);
    fetch(`http://localhost:9000/api/timesheet/delete-timesheet/${formState._id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((timesheetData) => {
        console.log("timesheetData:", timesheetData);

      });
  };

  return (
    <div>
      <h2>Edit/Delete</h2>
      <form onSubmit={handleSubmit}>

        <div>
        <label>
        <div className="nametxt">Name</div> 
          <input
            name="name"
            value={formState.name}
            onChange={handleChange}
          ></input>
        </label>
        </div>
        <div>
        <label>
        <div className="timetxt">Time</div> 
          <input
            name="time"
            value={formState.time}
            onChange={handleChange}
          ></input>
        </label>
        </div>
        <div>
        <label>
        <div className="notestxt">Notes</div> 
          <input
            name="notes"
            value={formState.notes}
            onChange={handleChange}
          ></input>
        </label>
        </div>
        <div>
        <label>
        <div className="completedtxt">Completed</div> 
          <input
            name="completed"
            value={formState.completed}
            onChange={handleChange}
          ></input>
        </label>
        </div>
        <button className ="edit" type="submit" >Edit</button>
        <button className ="delete"onClick={handleDelete}>Delete</button>
      </form>
    </div>
  );
};

export default TimeFormEdit;
