import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Link,
  Switch,
  Route,
  useHistory,
} from "react-router-dom";
import TimeForm from "./TimeForm";
import TimeFormEdit from "./TimeFormEdit";
import { List } from "./List";
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';

const AddTimeForm = () => {
  const history = useHistory();
  const [formState, setFormState] = useState({
    date: "",
    staff_attendance: "",
    client: "",
    travel_information: "",
    arrival_time: "",
    departure_time: "",
    products_used: "",
    receipts: "",
    notes: "",
    action_next_visit: "",
    written_by: "",
  });

  const handleChange = (e) => {
    const newState = { ...formState };
    newState[e.target.name] = e.target.value;
    setFormState(newState);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:3000/api/timesheets", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        token: window.localStorage.getItem("token"),
      },
      body: JSON.stringify(formState),
    }).then((response) => {
      console.log("use clases: response:", response);
    });
  };

  return (
    <div>
      <h2>Add Timesheet</h2>
      <form onSubmit={handleSubmit}>

      <div> <TextField name="date" id="standard-basic" label="Date" value={formState.date} onChange={handleChange}/></div>
      <div> <TextField name="client" id="standard-basic" label="Client" value={formState.client}onChange={handleChange}/></div> 
      <div> <TextField name="staff_attendance" id="standard-basic" label="Staff Attendance" value={formState.staff_attendance} onChange={handleChange}/></div>    
      <div> <TextField name="arrival_time" id="standard-basic" label="Arrival Time" value={formState.arrival_time} onChange={handleChange}/></div>
      <div> <TextField name="departure_time" id="standard-basic" label="Departure Time" value={formState.departure_time} onChange={handleChange}/></div>
      <div> <TextField name="products_used" id="standard-basic" label="Products Used" value={formState.products_used} onChange={handleChange}/></div>
      <div> <TextField name="receipts" id="standard-basic" label="Receipts" value={formState.receipts} onChange={handleChange}/></div>
      <div> <TextField name="notes" id="standard-basic" label="Notes" value={formState.notes} onChange={handleChange}/></div>
      <div> <TextField name="action_next_visit" id="standard-basic" label="Action Next Visit" value={formState.action_next_visit} onChange={handleChange}/></div>
      <div> <TextField name="written_by" id="standard-basic" label="Written By" value={formState.written_by} onChange={handleChange}/></div>

      <button className="add" type="submit">Add</button>
      </form>
    </div>
  );
};

export default AddTimeForm;
