import TimeContainer from "./components/AddTimeForm";
import TimeFormEdit from "./components/TimeFormEdit";
import {UserForm} from "./components/User/UserForm"
import {List} from "./components/List"; 
import AddTimeForm from "./components/AddTimeForm";
import { BrowserRouter as Router, Link, Switch, Route, useHistory } from "react-router-dom";
import { useState } from "react";
import "./Style.css";



export const App = () => {

const [loggedIn, setLoggedIn] = useState(false)  
  const onLogOut = () => {
    const history = useHistory()
  localStorage.removeItem('token')
  // use history redirect 
  // delete local storage and redirect to login
}

  return (
    <Router>
      <div>
      <Link to="/home">Home</Link>
        <Link to="/add">Add</Link>
        <Link to="/">Login</Link>
        <Link onClick={onLogOut}>Logout</Link>
        <Switch>
        <Route exact path="/login">
            <UserForm />
          </Route>
          {loggedIn && <Route exact path="/add">
            <AddTimeForm />
          </Route>}
          <Route exact path="/timesheet/edit/:id" >
            <TimeFormEdit />
          </Route>
          <Route exact path="/">
            <UserForm />
          </Route>
          <Route exact path="/home">
            <h1>Current Timesheets</h1>
           <List /> 
          </Route>
        </Switch>
      </div>
    </Router>
  );
};
